export default {
  ws: null,
  code_200: "连接成功",
  code_402: "聊天服务器已断开",
  code_404: "连接聊天服务器失败.请稍后重新刷新页面",
  code_403: "您的浏览器不支持 WebSocket",
  res: null,
  connect_sock: function connect_sock(id) {
    var _this = this;

    // 浏览器不支持 WebSocket
    if (!("WebSocket" in window)) {
      return this.res = this.code_403;
    }

    try {
      this.ws = new WebSocket("".concat(process.env.VUE_APP_WS_URL).concat(id));

      this.ws.onopen = function () {
        _this.res = _this.code_200;
      }; // 连接断开


      this.ws.onclose = function () {
        _this.res = _this.code_402;
      };
    } catch (e) {
      this.res = this.code_404;
    }
  }
};
