// 在元素上添加点击水波纹效果
import Vue from 'vue';
/* eslint-disable func-names */

Vue.directive('ripple', {
  inserted: function inserted(el, binding) {
    var cl = Array.from(el.classList);

    if (cl.includes('disabled')) {
      return;
    }

    var background = 'rgba(0,0,0,.3)';
    el.setAttribute('ripple', 'ripple');
    /* eslint no-param-reassign: 0 */

    el.style.position = 'relative';

    if (binding.value) {
      background = binding.value;
    }

    var isMobile = window.navigator.userAgent.match(/Mobile/) && window.navigator.userAgent.match(/Mobile/)[0] === 'Mobile';
    var event = isMobile ? 'touchstart' : 'click';
    el.addEventListener(event, function (e) {
      // e.preventDefault()
      var button = el;
      var rect = button.getBoundingClientRect();
      var btnWidth = rect.width;
      var posMouseX = 0;
      var posMouseY = 0;

      if (isMobile) {
        posMouseX = e.changedTouches[0].pageX - rect.left;
        posMouseY = e.changedTouches[0].pageY - rect.top;
      } else {
        posMouseX = e.x - rect.left;
        posMouseY = e.y - rect.top;
      }

      var baseCSS = "\n        position: absolute; width: ".concat(btnWidth * 2, "px; height: ").concat(btnWidth * 2, "px; transition: all linear 1000ms; \n        transition-timing-function:cubic-bezier(0.250, 0.460, 0.450, 0.940);border-radius: 50%; \n        background: ").concat(background, "; top:").concat(posMouseY - btnWidth, "px; left:").concat(posMouseX - btnWidth, "px; \n        pointer-events: none; transform:scale(0)");
      var rippleEffect = document.createElement('span');
      rippleEffect.style.cssText = baseCSS;
      button.style.overflow = 'hidden';
      this.appendChild(rippleEffect);
      setTimeout(function () {
        rippleEffect.style.cssText = "".concat(baseCSS, "transform:scale(1); opacity: 0;");
      }, 10);
      setTimeout(function () {
        rippleEffect.remove();
      }, 1000);
    });
  }
});
export default Vue.directive('ripple');
