import {
  Category
} from '@/model/category'
import CategoryEdit from '@/view/category/category-edit'

export default {
  components: {
    CategoryEdit,
  },
  data() {
    return {
      showEdit: false,
      tableData: [],
      totalNums: 0,
      currentPage: 1,
      pageCount: 10,
      refreshPagination: true,
      editCategoryID: 0,
    }
  },
  async created() {
    this.loading = true
    this.getAll()
    this.loading = false
  },
  methods: {
    async getAll() {
      const page = this.currentPage - 1
      const count = this.pageCount
      const categorys = await Category.getAll(page, count)
      this.tableData = categorys.items
      this.totalNums = categorys.total
    },
    // 切换table页
    async handleCurrentChange(val) {
      this.currentPage = val
      this.loading = true
      this.getAll()
      this.loading = false
    },
    handleEdit(val) {
      this.showEdit = true
      this.editCategoryID = val.id
    },
    handleDelete(val) {
      this.$confirm('是否删除该职位分类?', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning',
      }).then(async () => {
        const res = await Category.delete(val.id)
        if (res.code < window.MAX_SUCCESS_CODE) {
          this.getAll()
          this.$message({
            type: 'success',
            message: `${res.message}`,
          })
        }
      })
    },
    rowClick() { },
    editClose() {
      this.showEdit = false
      this.getAll()
    },
  },
}
