result:
.ws-msg-form {
  width: 800px;
  height: 700px;

  img {
    user-select: none;
    pointer-events: none;
    border: 1px solid #ffffff;
  }
}

.msg-content {
  margin-left: 10px;
  display: inline-block;
  width: 100%;
  height: 80%;
  border: 1px solid rgb(143, 141, 141);
  background-color: transparent !important;
  border-radius: 5px;
  overflow: hidden;
  position: relative;
}

.msg-title {
  position: absolute;
  top: 0;
  text-align: center;
  width: 100%;
  background-color: rgba(39, 39, 39, 0.219);
  height: 30px;
  line-height: 30px;
}

.msg-content .left {
  margin-top: 30px;
  width: 200px;
  height: 95%;
  border-right: 1px solid rgb(143, 141, 141);
  overflow: auto;
  overflow-x: hidden;
  display: inline-block;

  &::-webkit-scrollbar {
    width: 5px;
  }

  &::-webkit-scrollbar-button {
    display: none;
  }

  &::-webkit-scrollbar-thumb {
    background-color: rgba(124, 124, 124, 0.438);
    border-radius: 5px;
  }

  .item {
    position: relative;
    height: 78px;
    padding-left: 20px;
    display: flex;
    align-items: center;
    cursor: pointer;
    user-select: none;

    &:hover {
      background-color: rgba(146, 146, 146, 0.3);
    }

    .icon {
      width: 40px !important;
      height: 40px !important;
      border-radius: 40px;
    }

    div {
      position: absolute;
      top: 20px;
      left: 65px;
      font-size: 14px;
    }

    .msg {
      top: 45px;
      font-size: 12px;
      color: rgb(114, 114, 114);
      overflow: hidden;
      text-overflow: ellipsis;
      white-space: nowrap;
      max-width: 130px;
    }

    .newTip,
    .online {
      display: inline-block;
      width: 10px;
      height: 10px;
      background: red;
      border-radius: 10px;
      position: absolute;
      left: 10px;
      top: 20px;
    }

    .online {
      background-color: greenyellow;
      top: 50px;
      left: 20px;
    }

    .chat-time {
      display: inline-block;
      font-size: 13px;
      position: absolute;
      right: 5px;
      top: 5px;
      opacity: 0.7;
    }
  }
}

.timestamp {
  text-align: center;
  font-size: 12px;
  color: #999;
  margin: 8px 0 2px 0;
  padding: 0 5px;
  clear: both;
  width: 100%;
}

/* Adjust message item spacing to accommodate the timestamp */
.msg-item {
  width: 100%;
  margin: 5px 0;
  position: relative;
  transition: all 0.5s;
  clear: both;
}

.msg-content .right {
  display: inline-block !important;
  height: 94% !important;
  width: 600px !important;
  position: absolute !important;
  margin-top: 30px;

  .form {
    position: relative;
    height: 100%;
    width: 100%;

    .content {
      height: 385px;
      overflow: auto;
      padding: 0 10px;
      background-color: rgba(0, 0, 0, 0.03) !important;

      &::-webkit-scrollbar {
        overflow-y: hidden;
      }

      .msg-item {
        width: 100%;
        margin: 10px 0;
        position: relative;
        transition: all 0.5s;

        > div img {
          display: inline-block;
          width: 35px;
          height: 35px;
        }

        span {
          display: inline-block;
          background: white;
          padding: 10px 15px;
          font-size: 15px;
          max-width: 350px;
          margin: 10px 0;
          word-break: break-word;
        }

        .tip {
          width: 400px;
          height: 260px;
          border-radius: 10px;
          background-color: white;
          margin-left: 60px;
        }

        .sys,
        .target {
          float: left;
          margin-left: 10px;
          width: 100%;

          span {
            border-radius: 20px;
            border-top-left-radius: 0;
            margin-left: 35px;
          }

          img {
            position: absolute;
            left: 0;
          }
        }

        .this {
          position: relative;
          float: right;
          width: 100%;

          span {
            float: right;
            background-color: rgb(128, 185, 242);
            margin-right: 45px;
            color: white;
            border-radius: 20px;
            border-top-right-radius: 0;
          }

          img {
            position: absolute;
            right: 0;
          }
        }

        .birth-anim {
          animation-name: birth;
          animation-duration: 0.5s;
          animation-iteration-count: 1;
        }

        // 样式穿透
        ::v-deep .sys-msg {
          > * {
            margin: 15px 0;
            color: #505050;
            font-size: 15px;
          }

          h2 {
            font-weight: 700;
            font-size: 18px;
          }

          .info {
            width: 400px;

            span {
              display: inline-block;
              margin-bottom: 10px;
            }

            span:nth-child(1) {
              width: 100px !important;
              opacity: 0.7;
            }
          }
        }

        @keyframes birth {
          0% {
            transform: translatey(50px);
          }

          100% {
            transform: translatey(0px);
          }
        }
      }
    }
  }

  .msg-footer {
    position: relative;
    border-top: 1px solid rgb(143, 141, 141);

    textarea {
      resize: none;
      outline: none;
      width: 100%;
      height: 100px;
      border: none;
    }

    .send {
      position: absolute;
      right: 10px;
    }
  }
}

.select-style {
  background-color: rgba(146, 146, 146, 0.3);
}

.rou-btn {
  margin-top: 5px;
  display: inline-block;
  border: 1px solid cornflowerblue;
  color: cornflowerblue;
  border-radius: 5px;
  padding: 3px 5px;

  &:hover {
    border: 1px solid teal;
    color: teal;
  }
}
