import {
  Category
} from '@/model/category'

export default {
  components: {},
  // 定义变量
  props: {
    // 子组件定义的变量通过父组件赋值
    editCategoryID: {
      type: Number,
    },
  },
  data() {
    return {
      loading: false,
      form: {},
    }
  },
  async mounted() {
    this.loading = true
    const res = await Category.getById(this.editCategoryID)
    this.form = res
    this.loading = false
  },
  methods: {
    async submitForm() {
      const res = await Category.update(this.editCategoryID, this.form)
      if (res.code < window.MAX_SUCCESS_CODE) {
        this.$message.success(`${res.message}`)
        // 返回分类列表页面
        this.$emit('editClose')
      }
    },
    // 重置表单
    resetForm(formName) {
      this.$refs[formName].resetFields()
    },
    back() {
      // 由于showEdit这个变量是在父组件定义的，因此不能在子组件改变该变量的值，可以使用事件传递的方式，当点击了返回按钮，触发的事件处理机制交给父组件去处理
      this.$emit('editClose')
    },
  },
}
