import { Favor } from '@/model/favor'
import PositionDetail from '@/view/position/position-detail'

export default {
  components: {
    PositionDetail,
  },
  data() {
    return {
      loading: false,
      tableData: [],
      showEdit: false,
      detailPositionID: 0,
    }
  },
  async created() {
    this.loading = true
    await this.getFavorByUserId()
    this.loading = false
  },
  methods: {
    async getFavorByUserId() {
      try {
        const favors = await Favor.getFavorByUserId(this.$store.getters.user.id)
        this.tableData = favors
      } catch (error) {
        if (error.code === 40000) {
          this.tableData = []
        }
      }
    },
    handleDetail(val) {
      this.showEdit = true
      this.detailPositionID = val.id
    },
    handleDelete(val) {
      this.$confirm('是否取消收藏该职位?', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning',
      }).then(async () => {
        const res = await Favor.cancel(this.$store.getters.user.id, val.id)
        if (res.code < window.MAX_SUCCESS_CODE) {
          this.getFavorByUserId()
          this.$message({
            type: 'success',
            message: `${res.message}`,
          })
        }
      })
    },
    rowClick() { },
    detailClose() {
      this.showEdit = false
      this.getFavorByUserId()
    },
  },
}
