import LinSearch from '@/component/base/search/lin-search'
import { Company } from '@/model/company'
import { Follow } from '@/model/follow'
import CompanyDetail from '@/view/company/company-detail'

export default {
  components: {
    LinSearch,
    CompanyDetail,
  },
  data() {
    return {
      showDetail: false, // 是否显示公司详情页
      tableData: [], // 表格数据
      totalNums: 0, // 数据总数
      currentPage: 1, // 当前页数
      pageCount: 10, // 每页的数据量
      refreshPagination: true, // 页数增加的时候，因为缓存的缘故，需要刷新 Pagination 组件
      detailCompanyID: 0, // 公司id
      searchKeyword: '', // 搜索时输入的关键字
      keyword: null, // 查询关键字,用于显示搜索提示
      list: {}, //关注公司时传入的列表数据
    }
  },
  async created() {
    this.loading = true
    this.getByState()
    this.loading = false
  },
  watch: {
    /**
     * 关键字搜索
     */
    searchKeyword(newVal) {
      if (newVal) {
        this.keyword = newVal
        if (this.searchUser) {
          this.keyword = `${newVal}`
        }
      } else {
        this.keyword = ''
        this.$refs.searchKeyword.clear()
      }
      this.searchCompanyList()
    },
  },
  methods: {
    async getByState() {
      const page = this.currentPage - 1
      const count = this.pageCount
      // 展示所有审核通过的公司
      const companys = await Company.getByState(1, page, count)
      this.tableData = companys.items
      this.totalNums = companys.total
    },
    // 通过查询关键字分页搜索公司列表 
    async searchCompanyList() {
      const page = this.currentPage - 1
      const count = this.pageCount
      const companys = await Company.getByKeyword(page, count, this.searchKeyword)
      this.tableData = companys.items
      this.totalNums = companys.total
    },
    // 清空检索
    async backInit() {
      this.searchKeyword = ''
      this.keyword = ''
      this.tableData = []
      this.loading = true
      this.getByState()
      this.loading = false
    },
    /**
     * 监听输入查询关键字
     */
    async onQueryChange(query) {
      this.searchKeyword = query.trim()
    },
    // 切换table页
    async handleCurrentChange(val) {
      this.currentPage = val
      this.loading = true
      this.getByState()
      this.loading = false
    },
    handleDetail(val) {
      this.showDetail = true
      this.detailCompanyID = val.id
    },
    // 关注公司
    async handleFollow(val) {
      // 获取当前登录求职者的用户id
      this.list.user_id = this.$store.getters.user.id
      // 获取当前关注的公司id
      this.list.company_id = val.id
      // 调用添加关注的方法
      const res = await Follow.create(this.list)
      if (res.code < window.MAX_SUCCESS_CODE) {
        this.$message.success(`${res.message}`)
      }
    },
    detailClose() {
      this.showDetail = false
      this.getByState()
    },
  },
}
