import Admin from '@/lin/model/admin'
import User from '@/lin/model/user'

export default {
  props: {
    submit: {
      type: Boolean, // 表单是否显示功能按钮
      default: true,
    },
    id: {
      // 用户id
      type: Number,
      default: undefined,
    },
    groups: {
      // 所有分组
      type: Array,
      default: () => [],
    },
    labelPosition: {
      // 表单label位置
      type: String,
      default: 'right',
    },
    info: {
      // 用户信息
      type: Object,
      default: () => ({}),
    },
    pageType: {
      type: String,
      default: 'add', // 区分编辑页面/添加页面
    },
  },
  inject: ['eventBus'],
  computed: {
    filteredGroups() {
      // 如果用户组ID是1或5，显示所有角色
      if (this.userGroupId === 1 || this.userGroupId === 5) {
        return this.groups;
      }

      // 否则过滤掉ID为5的角色
      return this.groups.filter(group => group.id !== 5);
    }
  },

  data() {
    // 验证回调函数
    const checkUserName = (rule, value, callback) => {
      // eslint-disable-line
      if (!value) {
        callback(new Error('用户名不能为空'))
      }
      callback()
    }
    const validatePassword = (rule, value, callback) => {
      if (value === '') {
        callback(new Error('请输入密码'))
      } else if (value.length < 6) {
        callback(new Error('密码长度不能少于6位数'))
      } else {
        if (this.form.checkPassword !== '') {
          this.$refs.form.validateField('confirm_password')
        }
        callback()
      }
    }
    const validatePassword2 = (rule, value, callback) => {
      if (value === '') {
        callback(new Error('请再次输入密码'))
      } else if (value !== this.form.password) {
        callback(new Error('两次输入密码不一致!'))
      } else {
        callback()
      }
    }
    return {
      loading: false, // 加载动画
      isEdited: false, // 能否编辑
      form: {
        username: '',
        password: '',
        tel: '',
        confirm_password: '',
        email: '',
        nickname: '',
        group_ids: [],
      },
      userGroupId: null, // 当前用户的组ID
      // 验证规则
      rules: {
        nickname: [
          {
            trigger: ['blur', 'change'],
            required: true,
          },
        ],
        tel: [
          {
            trigger: ['blur', 'change'],
            required: true,
          },
        ],
        username: [
          {
            validator: checkUserName,
            trigger: ['blur', 'change'],
            required: true,
          },
        ],
        password: [{ validator: validatePassword, trigger: 'blur', required: true }],
        confirm_password: [{ validator: validatePassword2, trigger: 'blur', required: true }],
        email: [
          {
            type: 'email',
            message: '请输入正确的邮箱地址或者不填',
            trigger: ['blur', 'change'],
          },
        ],
      },
    }
  },
  methods: {
    // 获取当前用户的组ID
    async fetchUserGroup() {
      try {
        const res = await User.getGroup();
        this.userGroupId = res; 
      } catch (error) {
        console.error('获取用户组信息失败', error);
      }
    },

    // 提交表单
    async submitForm(formName) {
      this.$refs[formName].validate(async valid => {
        // eslint-disable-line
        if (valid) {
          // 新增用户
          let res
          if (this.pageType === 'add') {
            try {
              this.loading = true
              res = await User.register(this.form)
              if (res.code < window.MAX_SUCCESS_CODE) {
                this.loading = false
                this.$message.success(`${res.message}`)
                this.eventBus.$emit('addUser', true)
                this.resetForm(formName)
              }
            } catch (e) {
              this.loading = false
              if (e.data.code === 10073) {
                this.$message.error(e.data.message)
              } else {
                this.$message.error('新增用户失败')
              }
              console.log(e)
            }
          } else {
            // 更新用户信息
            if (
              this.form.email === this.info.email
              && this.form.group_ids.sort().toString() === this.info.group_ids.sort().toString()
            ) {
              this.$emit('handleInfoResult', false)
              return
            }
            try {
              if (!this.form.group_ids.length) {
                this.$message.error('至少选择一个角色')
                return
              }
              this.loading = true
              res = await Admin.updateOneUser(this.form.email, this.form.group_ids, this.id)
            } catch (e) {
              this.loading = false
              console.log(e)
            }
            if (res.code < window.MAX_SUCCESS_CODE) {
              this.loading = false
              this.$message.success(`${res.message}`)
              this.$emit('handleInfoResult', true)
            } else {
              this.loading = false
              this.$message.error(`${res.message}`)
            }
          }
        } else {
          console.log('error submit!!')
          this.$message.error('请填写正确的信息')
        }
      })
    },

    // 重置表单
    resetForm(formName) {
      if (this.pageType === 'edit') {
        this.setInfo()
      } else {
        this.form.group_ids = []
        this.$refs[formName].resetFields()
      }
    },

    // 设置用户信息
    setInfo() {
      this.form.username = this.info.username
      this.form.email = this.info.email
      this.form.tel = this.info.tel
      const temp = []
      this.info.group_ids.forEach(item => {
        temp.push(item.id)
      })
      this.form.group_ids = temp
    },
  },

  // 生命周期=
  async created() {
    // 获取当前用户组信息
    await this.fetchUserGroup();

    // 根据页面类型初始化
    if (this.pageType === 'edit') {
      this.setInfo()
      this.isEdited = true
    }
  }
}
