import { Position } from '@/model/position'
import PositionModify from '@/view/position/position-modify'

export default {
  components: {
    PositionModify,
  },
  data() {
    return {
      loading: false,
      tableData: [],
      totalNums: 0,
      currentPage: 1,
      pageCount: 10,
      refreshPagination: true,
      showEdit: false,
      editPositionID: 0,
    }
  },
  async created() {
    this.loading = true
    await this.getByHrId()
    this.loading = false
  },
  methods: {
    async getByHrId() {
      try {
        const page = this.currentPage - 1
        const count = this.pageCount
        const res = await Position.getByHrId(this.$store.getters.user.id, page,count)
        this.tableData = res.items
        this.totalNums = res.total
      } catch (error) {
        this.tableData = []
      }
    },
    // 对发布时间列进行格式化
    dateFormat(row, column) {
      return row['release_date'].substring(0, 10)
    },
    // 对职位状态列进行格式化
    stateFormat(row, column) {
      if (row['state'] === 0) {
        return "未审核";
      } else if (row['state'] === 1) {
        return "审核通过";
      } else {
        return "审核不通过";
      }
    },
    // 切换table页
    async handleCurrentChange(val) {
      this.currentPage = val
      this.loading = true
      this.getByHrId()
      this.loading = false
    },
    handleEdit(val) {
      this.showEdit = true
      this.editPositionID = val.id
    },
    handleDelete(val) {
      this.$confirm('是否下架该职位?', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning',
      }).then(async () => {
        const res = await Position.delete(val.id)
        if (res.code < window.MAX_SUCCESS_CODE) {
          this.getByHrId()
          this.$message({
            type: 'success',
            message: `${res.message}`,
          })
        }
      })
    },
    rowClick() { },
    editClose() {
      this.showEdit = false
      this.getByHrId()
    },
  },
}
