import {
  Application
} from '@/model/application'
import PositionDetail from '@/view/position/position-detail'
export default {
  components: {
    PositionDetail,
  },
  data() {
    return {
      flag: true,
      showPositionDetail: false,
      tableData: [],
      totalNums: 0,
      currentPage: 1,
      pageCount: 12,
      refreshPagination: true,
      showPositionID: 0,
      stateList: {
        "0":{"state":"待查看"  ,"type":"info"},
        "1":{"state":"已查看"  ,"type":""},
        "2":{"state":"通过初筛","type":"success"},
        "3":{"state":"邀面试"  ,"type":"warning"},
        "4":{"state":"未通过"  ,"type":"danger"},
      }
    }
  },
  async created() {
    this.loading = true
    this.getByUserId()
    this.loading = false
  },
  methods: {
    editClose() {
      this.showPositionDetail = false
      this.$router.push("/box/wait")
    },
    handleShow(val) {
      this.showPositionDetail = true
      this.showPositionID = val.id
    },
    async getByUserId() {
      const page = this.currentPage - 1
      const count = this.pageCount
      // 待处理：state=0
      const applications = await Application.getByUserId(this.$store.getters.user.id, count, page)
      this.tableData = applications.items
      this.totalNums = applications.total
    },
    // 切换table页
    async handleCurrentChange(val) {
      this.currentPage = val
      this.loading = true
      this.getByUserId()
      this.loading = false
    },
    checkState(state){
      if (state >= 2){
        return false;
      }
      return true;
    },
    // 撤销申请
    handleCancel(val) {
      this.$confirm('是否撤销申请?', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning',
      }).then(async () => {
        const res = await Application.delete(val.id)
        if (res.code < window.MAX_SUCCESS_CODE) {
          this.getByUserId()
          this.$message({
            type: 'success',
            message: `${res.message}`,
          })
        }
      })
    },
  },
}
