import { Application } from '@/model/application'

export default {
  data() {
    return {
      tableData: [],
      totalNums: 0,
      currentPage: 1,
      pageCount: 10,
      refreshPagination: true,
    }
  },
  async created() {
    this.loading = true
    this.getByUserId()
    this.loading = false
  },
  methods: {
    async getByUserId() {
      const page = this.currentPage - 1
      const count = this.pageCount
      // 待沟通：state=1
      const applications = await Application.getByUserId(this.$store.getters.user.id, 2, this.currentPage, this.pageCount)
      this.tableData = applications.items
      this.totalNums = applications.total
    },
    // 切换table页
    async handleCurrentChange(val) {
      this.currentPage = val
      this.loading = true
      this.getByUserId()
      this.loading = false
    },
  },
}
