import { Application } from '@/model/application'
import ResumeDetail from '@/view/resume/resume-detail'

export default {
  name: "ApplicationWait",
  components: {
    ResumeDetail
  },
  data() {
    return {
      showDetail: false,
      tableData: [],
      totalNums: 0,
      currentPage: 1,
      pageCount: 10,
      refreshPagination: true,
      detailResumeID: "",
      applicationID: "",
      applicationState: "",
    }
  },
  async created() {
    this.loading = true
    this.getByHrId()
    this.loading = false
  },
  methods: {
    async getByHrId() {
      const page = this.currentPage - 1
      const count = this.pageCount
      // 未处理：state=0
      const applications = await Application.getByHrId(this.$store.getters.user.id, 0,page,count)
      this.tableData = applications.items
      this.totalNums = applications.total
    },
    // 切换table页
    async handleCurrentChange(val) {
      this.currentPage = val
      this.loading = true
      this.getByHrId()
      this.loading = false
    },
    handleDetail(val) {
      this.showDetail = true
      this.detailResumeID = val.resume_id + ""
      this.applicationID = val.id + ""
      this.applicationState = val.state + ""
    },
    async handlePass(val) {
      const res = await Application.updateState(val.id, 1)
      if (res.code < window.MAX_SUCCESS_CODE) {
        this.$message.success(`${res.message}`)
        // 刷新页面
        this.getByHrId()
      }
    },
    async handleFail(val) {
      const res = await Application.updateState(val.id, 4)
      if (res.code < window.MAX_SUCCESS_CODE) {
        this.$message.success(`${res.message}`)
        // 刷新页面
        this.getByHrId()
      }
    },
    rowClick() { },
    detailClose() {
      this.showDetail = false
      this.getByHrId()
    },
  },
}
