import { Company } from '@/model/company'
import CompanyDetail from '@/view/company/company-detail'

export default {
  components: {
	CompanyDetail,
  },
  data() {
	return {
	  showDetail: false,
	  tableData: [],
	  imgSrcList: [], // 用于大图预览
	  totalNums: 0,
	  currentPage: 1,
	  pageCount: 10,
	  refreshPagination: true,
	  detailCompanyID: 0,
	}
  },
  async created() {
	this.loading = true
	this.getByState()
	this.loading = false
  },
  methods: {
	async getByState() {
	  const page = this.currentPage - 1
	  const count = this.pageCount
	  // 未审核：state=0
	  const companys = await Company.getByState(0, page, count)
	  this.tableData = companys.items
	  this.totalNums = companys.total
	  this.initImgSrcList()
	},
	// 切换table页
	async handleCurrentChange(val) {
	  this.imgSrcList = []
	  this.currentPage = val
	  this.loading = true
	  this.getByState()
	  this.loading = false
	},
	initImgSrcList() {
	  this.tableData.forEach(item => {
		if (!item.logo) {
		  return
		}
		this.imgSrcList.push(item.logo)
	  })
	},
	handleDetail(val) {
	  this.showDetail = true
	  this.detailCompanyID = val.id
	},
	async handlePass(val) {
	  const res = await Company.updateState(val.id, 1)
	  if (res.code < window.MAX_SUCCESS_CODE) {
		this.$message.success(`${res.message}`)
		// 刷新页面
		this.getByState()
	  }
	},
	async handleFail(val) {
	  const res = await Company.updateState(val.id, 2)
	  if (res.code < window.MAX_SUCCESS_CODE) {
		this.$message.success(`${res.message}`)
		// 刷新页面
		this.getByState()
	  }
	},
	rowClick() { },
	detailClose() {
	  this.showDetail = false
	  this.getByState()
	},
  },
}
