$nx-color: #449efe;
$all-padding: 0;
.Nav {
	width: 100%;
	height: 25rem;
	.contains {
		position: relative;
		width: 76.25rem;
		margin: 0 auto;
		.nav-left {
			margin-top: 1rem;
			.margin-top {
				width: 300px;
				height: 380px;
				>div {
    				box-shadow: 
						0px 0px 2px gainsboro,
                    	0px 0px 3px gainsboro,
                    	0px 0px 4px gainsboro;
					height: 100%;
					padding: 10px;
					text-align: left;
				}
				.user-data {
					span:nth-child(1) {
						display: inline-block;
						width: 100%;
						text-align: left;
					}
					span:nth-child(2) {
						display: inline-block;
						line-height: 30px;

						h6 {
							font-size: 16px;
							font-weight: 700;
						}
						div {
							font-size: 14px;
							opacity: .7;
						}
					}
					img {
						display: inline-block;
						width: 50px;
						margin-right: 15px;
					}
					.update-resume {
						position: absolute;
						top: 10px;
						left: 220px;
					}
				}
				.application {
					margin-top: 15px;
					text-align: center;
					>div>a{
						margin: 0 10px;
					}
					>div>span {
						display: inline-block;
						width: 33%;
						p {
							// margin-bottom: 10px;
							font-weight: 500;
							font-size: 24px;
							line-height: 34px;
							color: #5b7be9;
						}
						div {
							font-size: 12px;
							line-height: 17px;
							color: #333;
						}
					}
					button {
						margin-top: 10px;
						border-radius: 4px;
					}
					.btn_two {
						width: 150px;
						margin-left: 10px;
					}
					>p {
						border-top: 1px solid #00000017;
						padding-top: 10px;
					}
				}
				.describe {
					margin-top: 15px;
					list-style: 25px;
					height: 170px;
					.user-qr {
						text-align: center;

						img {
							width: 100px;
						}
					}
					>img {
						display: inline-block;
						height: 100%;
					}
					>P{
						font-weight: 700;
						font-size: 15px;
					}
					ul {
						overflow: auto;
						height: 97%;
					}
					ul::-webkit-scrollbar {
						width: 3px;
						overflow-x: hidden;
						overflow-y: hidden;
					}
					ul::-webkit-scrollbar-button{
						display: none;
					}
					ul::-webkit-scrollbar-thumb {
						background: #8b8b8b;
					}

					li {
						margin: 10px 0;
						font-size: 14px;
						padding: 5px;
						border-radius: 3px;
						cursor: pointer;
						border: 1px solid #c5c0c0;

						&:hover {
							background:#ebebeb;

							.esDate {
								color: rgb(0, 0, 0);
							}
						}

						.esSource,
						.esDate {
							display: inline-block;
							margin: 0 0 5px 5px;
						}
						.esSource {
							margin-left: 0;
						}
						.esDate {
							float: right;
							font-family: cursive;
							color: white;
							transition: color .5s;
						}
					}
				}
			}
		}
		.nav-right {
			position: absolute;
			right: 0;
			top: 0;
			width: 56rem;
			height: 27rem;
			img {
				height: 100%;
			}
			.work-list {
				position: absolute;
				width: 200px;
				height: 100%;
				background-color: rgba(79,90,102,.6);
				color: #fff;
				z-index: 99;
				overflow: auto;

				&::-webkit-scrollbar {
					width: 1px;
					transition: all .5s;
					opacity: 0;
				}
				&::-webkit-scrollbar-thumb {
					border-radius: 10px;
					background: rgba(22, 22, 22, 0.5);
				}
				&::-webkit-scrollbar-track {
					border-radius: 0;
					background: rgba(255, 255, 255);
				}
				&::-webkit-scrollbar-thumb:hover {
					width: 5px;
				}

				&:hover &::-webkit-scrollbar-thumb {
					opacity: 0;
				}
			}
			.work-li {
				position: relative;
				display: block;
				height: 3rem;
				line-height: 3rem;
				user-select: none;
				> span {
					font-size: 1rem;
					cursor: default;
				}
				&:hover,
				&:hover ul {
					display: block;
					background-color: rgba(0,0,0,.2);
					z-index: 10;
				}
			}
			// 当没有通过api获取到数据时渲染的li标签
			.nav-left-main-notList {
				border: 1px solid #dcdcdc;
			}
		}
	}
}
