import {
  Interview
} from '@/model/interview'
import ResumeDetail from '@/view/resume/resume-detail'
import moment from 'moment'
export default {
  name: "ApplicationWait",
  components: {
    ResumeDetail
  },
  data() {
    return {
      resultshow: false,
      dialogVisible: false,
      showDetail: false,
      tableData: [],
      totalNums: 0,
      currentPage: 1,
      pageCount: 10,
      refreshPagination: true,
      detailResumeID: 0,
      applicationID: 0,
      applicationState: 0,
      formData: {
        id: '',
        interviewdate: '',
        interviewTime: '',
        time: '',
        address: '',
        memo: '',
      },
      rules: {
        interviewdate: [{
          required: true,
          message: '请选择面试日期',
          trigger: 'change'
        }],
        interviewTime: [{
          required: true,
          message: '请选择面试时间',
          trigger: 'change'
        }],
        address: [{
          required: true,
          message: '请输入面试地点',
          trigger: 'blur'
        }],
        memo: [{
          required: true,
          message: '请输入备注',
          trigger: 'blur'
        }],
      },
    }
  },
  async created() {
    this.loading = true
    this.getByHrId()
    this.loading = false
  },
  methods: {
    onOpen(row) {
      this.dialogVisible = true
      this.formData = row
      this.formData.interviewdate = moment(`${row.time}`, 'YYYY-MM-DD').format();
      this.formData.interviewTime = moment(`${row.time}`, 'HH:mm:ss').format();
    },
    onClose() {
      this.$refs['elForm'].resetFields()
    },
    close() {
      this.dialogVisible = false
    },
    async handelConfirm() {
      this.$refs['elForm'].validate(valid => {
        if (!valid) return
        this.close()
      })
      this.formData.time = moment(`${this.interviewdate} ${this.interviewTime}`, 'YYYY-MM-DD HH:mm:ss').format();
      const res = await Interview.sendInterviewNotify(this.formData)
      if (res.code < window.MAX_SUCCESS_CODE) {
        this.$message.success(`发送成功`)
        this.getByHrId()
        this.close()
      }
    },
    async getByHrId() {
      const page = this.currentPage - 1
      const count = this.pageCount
      // 未处理：state=0
      const applications = await Interview.getByHrId(this.$store.getters.user.id, 0,page,count)
      this.tableData = applications.items
      this.tableData = this.tableData.filter((item) => {
        return item.status  < 2 
      });
      this.totalNums = applications.total
    },
    // 切换table页
    async handleCurrentChange(val) {
      this.currentPage = val
      this.loading = true
      this.getByHrId()
      this.loading = false
    },
    handleDetail(val) {
      this.showDetail = true
      this.detailResumeID = val.resume_id
      this.applicationID = val.id
      this.applicationState = val.state
    },
    rowClick() { },
    detailClose() {
      this.showDetail = false
      this.getByHrId()
    },
  },
}
