import { Resume } from '@/model/resume'
import { Application } from '@/model/application'
import Detail from './detail.vue'
export default {
  name: "ResumeDetail",
  components: {
    Detail
  },
  // 定义变量
  props: {
    detailResumeID: {
      type: String,
    },
    applicationID: {
      type: String,
    },
    applicationState: {
      type: String,
    }
  },
  data() {
    return {
      loading: false,
      form: {
      },
    }
  },
  async mounted() {
    try {
      this.loading = true
      const res = await Resume.getById(this.detailResumeID)
      this.form = res
      this.loading = false
    } catch (error) {
      this.loading = false
      console.log(error)
    }
  },
  methods: {
    async handlePass(val) {
      const res = await Application.updateState(this.applicationID, 1)
      if (res.code < window.MAX_SUCCESS_CODE) {
        this.$message.success(`${res.message}`)
        // 返回查询所有未处理简历的页面
        this.$emit('detailClose')
      }
    },
    async handleFail(val) {
      const res = await Application.updateState(this.applicationID, 2)
      if (res.code < window.MAX_SUCCESS_CODE) {
        this.$message.success(`${res.message}`)
        // 返回查询所有未处理简历的页面
        this.$emit('detailClose')
      }
    },
    back() {
      this.$emit('detailClose')
    },
  },
}
